-- tabla transaccional de informacion de tarjeta
CREATE TABLE tarjetas(
	ID VARCHAR(20) PRIMARY KEY NOT NULL,
	NOMBRE VARCHAR(100),
	EMAIL VARCHAR(100),
	TTA VARCHAR(16),
	BDATE DATE,
	STATE VARCHAR(50),
	ESTATUS INT,
	CREDIT NUMERIC(16,2)
);

